select cast(trv.order_index - trd.order_index as float) from minimize_test_result mtr join polluter_data pd on pd.minimized_id = mtr.id join dependency d on d.polluter_data_id = pd.id join test_result trd on trd.test_run_result_str_id = mtr.expected_run_str_id and trd.name = d.test_name join test_result trv on trv.test_run_result_str_id = mtr.expected_run_str_id and trv.name = mtr.test_name join test_run_result trr on trr.str_id = mtr.expected_run_str_id;
